const openstreetmapLayer = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"; // openstreetmap (Default)
const googleMapLayer = "https://mt1.google.com/vt/lyrs=y&x={x}&y={y}&z={z}"; // Google Map (Satellite)
const mapAttribution = 'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors';

const pointToLayer = (feature, latlng) => {
  const device = context.data[0].map((res) => (res));
  const filter = device.find((res) => JSON.parse(res.location).geometry.coordinates[0] === feature.geometry.coordinates[0] && JSON.parse(res.location).geometry.coordinates[1] === feature.geometry.coordinates[1])
  let customIcon;

  if (filter.connection) {
    customIcon = new L.Icon({
      iconSize: [41, 41],
      iconAnchor: [20, 41],
      popupAnchor: [0, -35],
      iconUrl: 'https://odn-grafana-image-bucket.s3.ap-northeast-2.amazonaws.com/marker/connected.png'
    });
  } else if (!filter.connection) {
    customIcon = new L.Icon({
      iconSize: [41, 41],
      iconAnchor: [20, 41],
      popupAnchor: [0, -35],
      iconUrl: 'https://odn-grafana-image-bucket.s3.ap-northeast-2.amazonaws.com/marker/disconnected.png'
    });
  }

  return L.marker(latlng, { icon: customIcon });
}

