options.styles:
#leaflet {
  width: 100%;
  height: ${Height};
  border-radius: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  align-self: center;
}

.status {
  position: absolute;
  // display: flex;
  z-index: 999;
  width: 150px;
  // height: 20%;
  left: 30px;
  bottom: 30px;
  background-color: rgba(0, 0, 0, 0.7);
  border-radius: 10px;
  // align-items: flex-end;
  justify-content: space-around;
  flex-direction: column;

  > span {
    font-weight: bold;
    color: white;
  }

  @media screen and (max-width: 50rem) {
    width: 25%;
    height: 8%;

    > span {
      font-weight: bold;
      color: white;
      font-size: 12px;
    }
  }
}

.status-icon {
  display: flex;
  align-items: center;
  margin: 5px 3px 5px 10px;
  justify-content: space-between;

  > span {
    display: flex;
  }

  > img {
    width: 35px;
    display: flex;
  }
}
